// Loop over input items and check for hosting suspension messages
for (const item of $input.all()) {
  try {
    // Get the response/content - adjust this field based on your HTTP Request node output
    const content = item.json.response?.body || 
                   item.json.content || 
                   item.json.data || 
                   '';

    // Convert to string if it's not already
    const pageContent = String(content).toLowerCase();

    const suspensionPhrases = [
      'Account Suspended',
      'suspended for non-payment',
      'subscription expired',
      'hosting suspended',
      'suspended due to payment',
      'account has been suspended',
      'website suspended',
      'this site has been suspended',
      'service is suspended',
      'please contact your hosting provider',
      'defaultwebpage'
    ];

    item.json.is_suspended = suspensionPhrases.some(phrase => 
      pageContent.includes(phrase)
    );

    // Add suspension details for debugging
    if (item.json.is_suspended) {
      item.json.suspension_reason = suspensionPhrases.find(phrase => 
        pageContent.includes(phrase)
      );
    }

  } catch (error) {
    // Handle any errors and mark the item as failed to check
    item.json.is_suspended = false;
    item.json.check_error = error.message;
  }
}

return $input.all();

