// Define the HTML structure with improved styling
const htmlTemplate = `
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Website Monitoring Report</title>
    <style>
        body {
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            line-height: 1.6;
            color: #333;
            max-width: 900px;
            margin: 0 auto;
            padding: 20px;
            background-color: #f5f7fa;
        }
        .container {
            background-color: #ffffff;
            border-radius: 10px;
            box-shadow: 0 4px 15px rgba(0,0,0,0.08);
            padding: 30px;
            border-top: 5px solid #3498db;
        }
        header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding-bottom: 20px;
            margin-bottom: 25px;
            border-bottom: 2px solid #f0f2f5;
        }
        .header-content {
            flex-grow: 1;
            margin-right: 30px;
        }
        .timestamp {
            background-color: #f8fafc;
            padding: 8px 12px;
            border-radius: 6px;
            font-size: 14px;
            color: #64748b;
        }
        .timestamp-container {
            display: flex;
            flex-direction: column;
            gap: 5px;
        }
        h2 {
            color: #1e3a8a;
            margin-top: 0;
            font-size: 26px;
            font-weight: 600;
        }
        table {
            width: 100%;
            border-collapse: separate;
            border-spacing: 0;
            margin: 20px 0;
            font-size: 15px;
            box-shadow: 0 2px 5px rgba(0,0,0,0.05);
            border-radius: 8px;
            overflow: hidden;
        }
        th, td {
            padding: 14px 18px;
            border: none;
            border-bottom: 1px solid #e5e7eb;
        }
        th {
            background-color: #f1f5f9;
            color: #334155;
            font-weight: 600;
            text-transform: uppercase;
            font-size: 13px;
            letter-spacing: 0.5px;
        }
        tr:last-child td {
            border-bottom: none;
        }
        tr:nth-child(even) {
            background-color: #fafbfc;
        }
        tr:hover {
            background-color: #f1f5fb;
        }
        .status-up {
            color: green;
            font-weight: bold;
        }
        .status-down {
            color: red;
            font-weight: bold;
        }
        .status-suspended {
            color: orange;
            font-weight: bold;
        }
        .website-column {
            width: 35%;
            text-align: left;
        }
        .status-column {
            width: 20%;
            text-align: left;
        }
        .code-column {
            width: 15%;
            text-align: center;
        }
        .message-column {
            width: 30%;
            text-align: center;
        }
        .website-link {
            color: #2563eb;
            text-decoration: none;
            font-weight: 500;
            display: block;
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
            max-width: 300px;
            transition: color 0.2s;
        }
        .website-link:hover {
            color: #1d4ed8;
            text-decoration: underline;
        }
        .code-badge {
            display: inline-block;
            padding: 4px 8px;
            background-color: #f1f5f9;
            border-radius: 4px;
            font-family: monospace;
            font-weight: 600;
        }
        .success-code {
            background-color: #ecfdf5;
            color: #065f46;
        }
        .error-code {
            background-color: #fef2f2;
            color: #991b1b;
        }
        .warning-code {
            background-color: #fffbeb;
            color: #92400e;
        }
        .footer {
            margin-top: 30px;
            text-align: center;
            font-size: 14px;
            color: #64748b;
            padding-top: 20px;
            border-top: 1px solid #e5e7eb;
        }
        .summary-section {
            display: flex;
            justify-content: space-around;
            margin-bottom: 25px;
            text-align: center;
        }
        .summary-box {
            padding: 15px;
            border-radius: 8px;
            width: 30%;
        }
        .summary-label {
            font-size: 14px;
            text-transform: uppercase;
            letter-spacing: 0.5px;
            margin-bottom: 5px;
            color: #64748b;
        }
        .summary-value {
            font-size: 24px;
            font-weight: 700;
        }
        .up-box {
            background-color: #f0fdf4;
            border: 1px solid #dcfce7;
        }
        .down-box {
            background-color: #fef2f2;
            border: 1px solid #fee2e2;
        }
        .suspended-box {
            background-color: #fffbeb;
            border: 1px solid #fef3c7;
        }
        .timestamp-label {
            font-weight: 600;
        }
    </style>
</head>
<body>
    <div class="container">
        <header>
            <div class="header-content">
                <h2>Website Monitoring Report</h2>
                <p>Monitoring the results for all our websites</p>
            </div>
            <div class="timestamp-container">
                <div class="timestamp">
                    <span class="timestamp-label">🇫🇷 France:</span> ${formatFrenchTime()} <br><br> <span class="timestamp-label">🇮🇳 India:</span> ${formatIndianTime()}
                </div>
            </div>
        </header>

        <div class="summary-section">
            <div class="summary-box up-box">
                <div class="summary-label">Up</div>
                <div class="summary-value">{{UP_COUNT}}</div>
            </div>
            <div class="summary-box down-box">
                <div class="summary-label">Down</div>
                <div class="summary-value">{{DOWN_COUNT}}</div>
            </div>
            <div class="summary-box suspended-box">
                <div class="summary-label">Suspended</div>
                <div class="summary-value">{{SUSPENDED_COUNT}}</div>
            </div>
        </div>

        <table>
            <thead>
                <tr>
                    <th class="website-column">Website</th>
                    <th class="status-column">Status</th>
                    <th class="code-column">Status Code</th>
                    <th class="message-column">Status Message</th>
                </tr>
            </thead>
            <tbody>
                {{TABLE_ROWS}}
            </tbody>
        </table>

        <div class="footer">
            <p>This is an automated status report generated on:</p>
            <p>🇫🇷 France: ${formatFrenchTime()} | 🇮🇳 India: ${formatIndianTime()}</p>
        </div>
    </div>
</body>
</html>
`;

// Function to format date in French timezone with DD/MM/YYYY format
function formatFrenchTime() {
    const now = new Date();

    // Format for France (Central European Time)
    const frenchOptions = { timeZone: 'Europe/Paris' };
    const frenchDate = now.toLocaleDateString('fr-FR', frenchOptions);
    const frenchTime = now.toLocaleTimeString('fr-FR', frenchOptions);

    return `${frenchDate} ${frenchTime}`;
}

// Function to format date in Indian timezone with DD/MM/YYYY format
function formatIndianTime() {
    const now = new Date();

    // Format for India (Indian Standard Time)
    const indianOptions = { timeZone: 'Asia/Kolkata' };
    const indianDate = now.toLocaleDateString('en-IN', indianOptions);
    const indianTime = now.toLocaleTimeString('en-IN', indianOptions);

    return `${indianDate} ${indianTime}`;
}

// Function to get status indicator - using your original code
function getStatusIndicator(status) {
    if (status === "UP") return `<span class="status-up">🟢 UP</span>`;
    if (status === "DOWN") return `<span class="status-down">🔴 DOWN</span>`;
    if (status === "SUSPENDED") return `<span class="status-suspended">🟡 SUSPENDED</span>`;
    return status;
}

// Function to get appropriate code badge styling
function getCodeBadgeClass(code) {
    const codeNum = parseInt(code);
    if (codeNum >= 200 && codeNum < 300) return "success-code";
    if (codeNum >= 400 && codeNum < 500) return "warning-code";
    if (codeNum >= 500) return "error-code";
    return "";
}

// Get input data from n8n
const inputData = $input.all(); 
let tableRows = "";

// Count statuses
let upCount = 0;
let downCount = 0;
let suspendedCount = 0;

// Loop through each item in the data
for (const item of inputData) {
    // Count statuses
    if (item.json.Status === "UP") upCount++;
    else if (item.json.Status === "DOWN") downCount++;
    else if (item.json.Status === "SUSPENDED") suspendedCount++;

    const statusCode = item.json["Status Code"];
    const codeBadgeClass = getCodeBadgeClass(statusCode);

    const row = `
        <tr>
            <td class="website-column">
                <a href="${item.json.Property}" target="_blank" class="website-link">${item.json.Property}</a>
            </td>
            <td class="status-column">${getStatusIndicator(item.json.Status)}</td>
            <td class="code-column">
                <span class="code-badge ${codeBadgeClass}">${statusCode}</span>
            </td>
            <td class="message-column">${item.json["Status Message"]}</td>
        </tr>
    `;
    tableRows += row;
}

// Replace placeholders in the HTML template
let finalHtml = htmlTemplate.replace("{{TABLE_ROWS}}", tableRows);
finalHtml = finalHtml.replace("{{UP_COUNT}}", upCount);
finalHtml = finalHtml.replace("{{DOWN_COUNT}}", downCount);
finalHtml = finalHtml.replace("{{SUSPENDED_COUNT}}", suspendedCount);

// Return the HTML output
return [{ json: { html: finalHtml } }];
