// Table template for each category
const tableTemplate = `
<table>
    <thead>
        <tr>
            <th class="website-column">Website</th>
            <th class="status-column">Status</th>
            <th class="code-column">Status Code</th>
            <th class="message-column">Status Message</th>
            <th class="visitors-column">Last 7 Days<br><span>visitors<span></th>
            <th class="visitors-column">Last 28 Days<br><span>visitors<span></th>
            <th class="metrics-column">Avg. Session<br><span>Duration<span></th>
            <th class="metrics-column">Bounce<br><span>Rate<span></th>
            <th class="metrics-column">Top<br><span>Country<span></th>
        </tr>
    </thead>
    <tbody>
        {{ROWS}}
    </tbody>
</table>
`;

// Function to format date in French timezone with DD/MM/YYYY format
function formatFrenchTime() {
    const now = new Date();

    // Format for France (Central European Time)
    const frenchOptions = { timeZone: 'Europe/Paris' };
    const frenchDate = now.toLocaleDateString('fr-FR', frenchOptions);
    const frenchTime = now.toLocaleTimeString('fr-FR', frenchOptions);

    return `${frenchDate} ${frenchTime}`;
}

// Function to format date in Indian timezone with DD/MM/YYYY format
function formatIndianTime() {
    const now = new Date();

    // Format for India (Indian Standard Time)
    const indianOptions = { timeZone: 'Asia/Kolkata' };
    const indianDate = now.toLocaleDateString('en-IN', indianOptions);
    const indianTime = now.toLocaleTimeString('en-IN', indianOptions);

    return `${indianDate} ${indianTime}`;
}

// Function to get status indicator
function getStatusIndicator(status) {
    if (status === "UP") return `<span class="status-up">🟢 UP</span>`;
    if (status === "DOWN") return `<span class="status-down">🔴 DOWN</span>`;
    if (status === "SUSPENDED") return `<span class="status-suspended">🟡 SUSPENDED</span>`;
    return status;
}

// Function to get appropriate code badge styling
function getCodeBadgeClass(code) {
    const codeNum = parseInt(code);
    if (codeNum >= 200 && codeNum < 300) return "success-code";
    if (codeNum >= 400 && codeNum < 500) return "warning-code";
    if (codeNum >= 500) return "error-code";
    return "";
}

// Define website categories
const gmgInternalSites = [
    "https://www.gmindia.tech/",
    "https://acetechinside.com/",
    "https://acetechinside.tech/",
    "https://www.become.institute/",
    "https://www.bimatech.io/",
    "https://quantumleap.paris/",
    "https://a2zformalites.com/"
];

const gmgExternalSites = [
    "http://a2zvisas.com/",
    "https://edunextparis.com/",
    "https://www.euronixa.eu/",
    "https://www.euronixa.fr/",
    "https://france.euronixa.eu/"
];

const clientSites = [
    "https://www.cleanformation.fr/",
    "https://monbateauprive.com/",
    "https://memoire-a-venir.org/",
    "https://preprod.gmindia.tech/mda/",
    "https://lourse.art/"
];

// Get input data from n8n
const inputData = $input.all(); 

// Initialize objects to store categorized data
const categories = {
    gmgInternal: [],
    gmgExternal: [],
    clients: []
};

// Count statuses
let upCount = 0;
let downCount = 0;
let suspendedCount = 0;

// Loop through each item in the data and categorize
for (const item of inputData) {
    // Count statuses
    if (item.json.Status === "UP") upCount++;
    else if (item.json.Status === "DOWN") downCount++;
    else if (item.json.Status === "SUSPENDED") suspendedCount++;

    // Get website URL
    const websiteUrl = item.json.Property;

    // Categorize based on website URL
    if (gmgInternalSites.includes(websiteUrl)) {
        categories.gmgInternal.push(item);
    } else if (gmgExternalSites.includes(websiteUrl)) {
        categories.gmgExternal.push(item);
    } else if (clientSites.includes(websiteUrl)) {
        categories.clients.push(item);
    } else {
        // If it doesn't match any predefined category, check for patterns
        if (websiteUrl.includes("gmindia") || 
            websiteUrl.includes("acetech") || 
            websiteUrl.includes("become.institute") || 
            websiteUrl.includes("bimatech") || 
            websiteUrl.includes("quantumleap") || 
            websiteUrl.includes("a2zformalites")) {
            categories.gmgInternal.push(item);
        } else if (websiteUrl.includes("a2zvisas") || 
                  websiteUrl.includes("edunext") || 
                  websiteUrl.includes("euronixa")) {
            categories.gmgExternal.push(item);
        } else {
            // If still can't categorize, add to clients as fallback
            categories.clients.push(item);
        }
    }
}

// Function to create a table row for a website
function createTableRow(item) {
    if (!item || !item.json) return "";

    const statusCode = item.json["Status Code"] || "";
    const codeBadgeClass = getCodeBadgeClass(statusCode);

    return `
        <tr>
            <td class="website-column">
                <a href="${item.json.Property}" target="_blank" class="website-link">${item.json.Property}</a>
            </td>
            <td class="status-column">${getStatusIndicator(item.json.Status || "")}</td>
            <td class="code-column">
                <span class="code-badge ${codeBadgeClass}">${statusCode}</span>
            </td>
            <td class="message-column">${item.json["Status Message"] || ""}</td>
            <td class="visitors-column">
                <span class="visitor-badge">${item.json["Visitor Last 7 Days"] || ""}</span>
            </td>
            <td class="visitors-column">
                <span class="visitor-badge">${item.json["Visitor Last 28 Days"] || ""}</span>
            </td>
            <td class="metrics-column">
                <span class="metrics-badge">${item.json["Avg. Session Duration"] || ""}</span>
            </td>
            <td class="metrics-column">
                <span class="metrics-badge">${item.json["Bounce Rate"] || ""}</span>
            </td>
            <td class="metrics-column">
                <span class="metrics-badge">${item.json["Top Country"] || ""}</span>
            </td>
        </tr>
    `;
}

// Function to generate table with rows or placeholder message
function generateTable(categoryItems) {
    if (categoryItems.length === 0) {
        return `<div class="empty-table-message">No websites currently monitored in this category</div>`;
    }

    let rows = "";
    for (const item of categoryItems) {
        rows += createTableRow(item);
    }

    return tableTemplate.replace("{{ROWS}}", rows);
}

// Generate tables for each category
const gmgInternalTable = generateTable(categories.gmgInternal);
const gmgExternalTable = generateTable(categories.gmgExternal);
const clientsTable = generateTable(categories.clients);

// Replace placeholders in the HTML template
let finalHtml = htmlTemplate
    .replace("{{GMG_INTERNAL_TABLE}}", gmgInternalTable)
    .replace("{{GMG_EXTERNAL_TABLE}}", gmgExternalTable)
    .replace("{{CLIENTS_TABLE}}", clientsTable)
    .replace("{{UP_COUNT}}", upCount)
    .replace("{{DOWN_COUNT}}", downCount)
    .replace("{{SUSPENDED_COUNT}}", suspendedCount);

// Return the HTML output
return [{ json: { html: finalHtml } }];

