// Loop over input items and check for various website issues
for (const item of $input.all()) {
  try {
    // Get the response/content - adjust this field based on your HTTP Request node output
    const content = item.json.response?.body || 
                   item.json.content || 
                   item.json.data || 
                   '';

    // Convert to string if it's not already
    const pageContent = String(content).toLowerCase();

    const suspensionPhrases = [
      'account suspended',
      'suspended for non-payment',
      'subscription expired',
      'hosting suspended',
      'suspended due to payment',
      'account has been suspended',
      'website suspended',
      'this site has been suspended',
      'service is suspended',
      'please contact your hosting provider',
      'defaultwebpage'
    ];

    // Common indicators of compromised websites
    const compromisedPhrases = [
      'hacked by',
      'site hacked',
      'website hacked',
      'defaced by',
      'pwned by',
      'owned by',
      'crypto miner',
      'unauthorized script',
      'malicious redirect',
      'suspicious iframe',
      'eval(base64_decode',
      'eval(unescape',
      'document.location.replace',
      'pharma hack',
      'hidden link',
      'spam seo',
      'blackhat seo'
    ];

    // WordPress specific errors
    const wordpressErrorPhrases = [
      'error establishing a database connection',
      'database connection error',
      'wordpress database error',
      'critical error on this website',
      'fatal error:',
      'uncaught error',
      'warning: cannot modify header',
      'wordpress installation error',
      'wp-config.php not found',
      'error 500',
      'wordpress setup configuration file',
      'the site is experiencing technical difficulties',
      'temporarily unavailable due to scheduled maintenance',
      'white screen of death'
    ];

    // Common website errors
    const generalErrorPhrases = [
      '404 not found',
      '403 forbidden',
      '500 internal server error',
      '502 bad gateway',
      '503 service unavailable',
      '504 gateway timeout',
      'bandwidth limit exceeded',
      'connection timed out',
      'dns error',
      'domain expired',
      'parking page',
      'site under construction',
      'coming soon',
      'site temporarily unavailable',
      'maintenance mode',
      'domain for sale',
      'this domain may be for sale',
      'this page is parked free',
      'domain parked',
      'website builder coming soon'
    ];

    // Check for suspension status
    item.json.is_suspended = suspensionPhrases.some(phrase => 
      pageContent.includes(phrase.toLowerCase())
    );

    // Add suspension details for debugging
    if (item.json.is_suspended) {
      item.json.suspension_reason = suspensionPhrases.find(phrase => 
        pageContent.includes(phrase.toLowerCase())
      );
    }

    // Check for compromise status
    item.json.is_compromised = compromisedPhrases.some(phrase => 
      pageContent.includes(phrase.toLowerCase())
    );

    // Add compromise details for debugging
    if (item.json.is_compromised) {
      item.json.compromise_indicator = compromisedPhrases.find(phrase => 
        pageContent.includes(phrase.toLowerCase())
      );
    }

    // Check for WordPress errors
    item.json.has_wordpress_error = wordpressErrorPhrases.some(phrase => 
      pageContent.includes(phrase.toLowerCase())
    );

    // Add WordPress error details
    if (item.json.has_wordpress_error) {
      item.json.wordpress_error_reason = wordpressErrorPhrases.find(phrase => 
        pageContent.includes(phrase.toLowerCase())
      );
    }

    // Check for general website errors
    item.json.has_website_error = generalErrorPhrases.some(phrase => 
      pageContent.includes(phrase.toLowerCase())
    );

    // Add general error details
    if (item.json.has_website_error) {
      item.json.website_error_reason = generalErrorPhrases.find(phrase => 
        pageContent.includes(phrase.toLowerCase())
      );
    }

    // Check for blank page
    // Remove HTML tags, whitespace, and common empty page indicators
    const cleanContent = pageContent
      .replace(/<[^>]*>/g, '')  // Remove HTML tags
      .replace(/\s+/g, ' ')     // Normalize whitespace
      .trim();                  // Remove leading/trailing whitespace

    // Define blank page conditions
    const isBlankOrNearly = 
      cleanContent.length === 0 || 
      cleanContent.length < 50 ||
      /^(\s*|index of\/|directory listing|not found|404|403|error|forbidden|no content)$/i.test(cleanContent);

    item.json.is_blank = isBlankOrNearly;

    // Add blank page details for debugging
    if (item.json.is_blank) {
      item.json.blank_reason = cleanContent.length === 0 ? 
        'Empty content' : 
        cleanContent.length < 50 ? 
          `Minimal content (${cleanContent.length} chars)` : 
          'Default/error page detected';
    }

    // Create a summary status
    item.json.site_status = 
      item.json.is_suspended ? 'suspended' :
      item.json.is_compromised ? 'compromised' :
      item.json.has_wordpress_error ? 'wordpress_error' :
      item.json.has_website_error ? 'website_error' :
      item.json.is_blank ? 'blank' : 'normal';

  } catch (error) {
    // Handle any errors and mark the item as failed to check
    item.json.is_suspended = false;
    item.json.is_compromised = false;
    item.json.is_blank = false;
    item.json.has_wordpress_error = false;
    item.json.has_website_error = false;
    item.json.site_status = 'check_failed';
    item.json.check_error = error.message;
  }
}
return $input.all();

